import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

var reptiles = ['alligator', 'snake', 'lizard'];

// class ItemList extends React.Component {
//     constructor(props) {
//       super(props);
//       this.state = {value: ''};
//       }

//     render(){
//         return (

//         ); 
//     }
// }



// Do stuff
class DoStuff extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        items: []
      };
    }

    addItem(e) {
        if (this._inputElement.value !== "") {
          var newItem = {
            text: this._inputElement.value,
            key: Date.now()
          };

          this.setState((prevState) => {
            return { 
              items: prevState.items.concat(newItem) 
            };
          });

          this._inputElement.value = "";
        }

        console.log(this.state.items);

        e.preventDefault();
      }

    render(){
        return (
            <div className="todoListMain">
            <div className="header">
              <form onSubmit={this.addItem}>
                <input ref={(a) => this._inputElement = a} 
                  placeholder="enter task"></input>
                <button type="submit">add</button>
              </form>
            </div>
            <TodoItems entries={this.state.items}/>
          </div>
        );
    }
}



// Makes the form appear in the modal, always
class Form extends React.Component {
    constructor(props) {
      super(props);
    }
    render(){
        return (
            <form id="form" onsubmit={this.addItem}>
          <input type="text" name="input" id="input" placeholder="Item"/>
          <input type="date" name="expiry" id="expiry"/>
          <input type="submit" class="btn btn-default" data-dismiss="modal"/>
        </form>
        );
    }
}



// ReactDOM Renders

ReactDOM.render(
    <div>
        <DoStuff />
    </div>,
    document.getElementById("list")
); 

ReactDOM.render(
    <div>
        <Form />
    </div>,
    document.getElementById("form-root")
);
