import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

var reptiles = ['alligator', 'snake', 'lizard'];

class ItemList extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};
      this.addItem = this.addItem.bind(this);
      }

    addItem(){
        var newItem = {
        text: this._inputElement.value,
        key: Date.now()
        };

        this.setState((prevState) => {
            return { 
              items: prevState.items.concat(newItem) 
            };
          });
          this._inputElement.value = "";



          console.log(this.state.items);

    }

    render(){
        return (
            <div>
                <div>
                    {reptiles}
                </div>
            </div>
        ); 
    }
}



// Do stuff
class DoStuff extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        items: []
      };
    }
    render(){
        return (
            <ItemList entries={this.state.items} delete={this.deleteItem}/>
        );
    }
}



// Makes the form appear in the modal, always
class Form extends React.Component {
    constructor(props) {
      super(props);
    }
    render(){
        return (
            <form id="form" onsubmit={this.addItem}>
          <input type="text" name="item" id="item" placeholder="Item"/>
          <input type="date" name="expiry" id="expiry"/>
          <input type="submit" class="btn btn-default" data-dismiss="modal"/>
        </form>
        );
    }
}



// ReactDOM Renders

ReactDOM.render(
    <div>
        <DoStuff />
    </div>,
    document.getElementById("list")
); 

ReactDOM.render(
    <div>
        <Form />
    </div>,
    document.getElementById("form-root")
);
