import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

/*ReactDOM.render(
    <div>
        <ToDo />
    </div>,
    document.getElementById("root")
); */

const reptiles = ['alligator', 'snake', 'lizard'];

const Todo = ({todo, remove}) => {
    // Each Todo
    return (<a href="#" className="list-group-item" onClick={() => {remove(todo.id)}}>{todo.text}</a>);
  }

const TodoList = ({todos, remove}) => {
    // Map through the todos
    const todoNode = todos.map((todo) => {
      return (<Todo todo={todo} key={todo.id} remove={remove}/>)
    });
    return (<div className="list-group" style={{marginTop:'30px'}}>{todoNode}</div>);
  }




  window.id = 0;
  class TodoApp extends React.Component{
    constructor(props){
      // Pass props to parent class
      super(props);
      // Set initial state
      this.state = {
        data: []
      }
      this.apiUrl = '//57b1924b46b57d1100a3c3f8.mockapi.io/api/todos'
    }
    // Lifecycle method
    componentDidMount(){
      // Make HTTP reques with Axios
      axios.get(this.apiUrl)
        .then((res) => {
          // Set state with result
          this.setState({data:res.data});
        });
    }
    // Add todo handler
    addTodo(val){
      // Assemble data
      const todo = {text: val}
      // Update data
      axios.post(this.apiUrl, todo)
         .then((res) => {
            this.state.data.push(res.data);
            this.setState({data: this.state.data});
         });
    }
    // Handle remove
    handleRemove(id){
      // Filter all todos except the one to be removed
      const remainder = this.state.data.filter((todo) => {
        if(todo.id !== id) return todo;
      });
      // Update state with filter
      axios.delete(this.apiUrl+'/'+id)
        .then((res) => {
          this.setState({data: remainder});      
        })
    }

    render(){
        // Render JSX
        return (
          <div>
            <Title todoCount={this.state.data.length}/>
            <TodoForm addTodo={this.addTodo.bind(this)}/>
            <TodoList 
              todos={this.state.data} 
              remove={this.handleRemove.bind(this)}
            />
          </div>
        );
      }
  }




class ItemForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
      this.setState({value: event.target.value});

    }

    render(){
        return (
            <form id="form" onSubmit={this.handleSubmit}>
                <input type="text" name="item" id="item" placeholder="Item"/>
                <input type="date" name="expiry-date"/>
                <input type="submit" class="btn btn-default" data-dismiss="modal" onclick={this.handleSubmit}/>
            </form>
        );
    }
}

class ItemList extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      //this.addItem = this.addItem.bind(this);
    }

    render(){
        return (
            <div>

            </div>
        ); // this returns the array containing the list of items
    }
}


ReactDOM.render(
    <div>
        <ItemForm />
    </div>, 
    document.getElementById("root")
);

ReactDOM.render(
    <div>
        <ItemList />
    </div>,
    document.getElementById("list")
);
