import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

/*ReactDOM.render(
    <div>
        <ToDo />
    </div>,
    document.getElementById("root")
); */

var reptiles = ['alligator', 'snake', 'lizard'];

class ItemForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
      this.setState({value: event.target.value});
      let newItem = document.getElementById("item").value;
      reptiles.push(newItem);
    }

    render(){
        return (
            <form id="form" onSubmit={this.handleSubmit}>
                <input type="text" name="item" id="item" placeholder="Item"/>
                <input type="date" name="expiry-date"/>
                <input type="submit" class="btn btn-default" data-dismiss="modal" onclick={this.handleSubmit}/>
            </form>
        );
    }
}

class ItemList extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      //this.addItem = this.addItem.bind(this);
    }

    render(){
        return (
            <div>
                {reptiles}
            </div>
        ); // this returns the array containing the list of items
    }
}


ReactDOM.render(
    <div>
        <ItemForm />
    </div>, 
    document.getElementById("root")
);

ReactDOM.render(
    <div>
        <ItemList />
    </div>,
    document.getElementById("list")
);
